CREATE OR REPLACE PROCEDURE DEQ_{0}
(
    messageType OUT VARCHAR2,
    messageContent OUT XMLType
)
AS
    dequeue_options      dbms_aq.dequeue_options_t;
    message_properties   dbms_aq.message_properties_t;
    message_handle       RAW(16);
    message              TYPE_{0};
    no_messages          EXCEPTION;
    PRAGMA EXCEPTION_INIT(no_messages, -25228);
BEGIN
    dequeue_options.CONSUMER_NAME           := NULL;
    dequeue_options.DEQUEUE_MODE            := DBMS_AQ.REMOVE;
    dequeue_options.NAVIGATION              := DBMS_AQ.NEXT_MESSAGE;
    dequeue_options.VISIBILITY              := DBMS_AQ.IMMEDIATE;
    dequeue_options.WAIT                    := {1};
    dequeue_options.MSGID                   := NULL;

    BEGIN
        DBMS_AQ.DEQUEUE (
            queue_name         => 'QUE_{0}'
            , dequeue_options    => dequeue_options
            , message_properties => message_properties
            , payload            => message
            , msgid              => message_handle);

        messageType := message.MESSAGE_TYPE; 
        messageContent := message.MESSAGE_CONTENT;
    EXCEPTION
        WHEN no_messages THEN
            messageType := '{2}';
    END;
END;

BEGIN
    DBMS_AQADM.CREATE_QUEUE_TABLE(queue_table=> 'QT_{0}', queue_payload_type=> 'TYPE_{0}', multiple_consumers => FALSE, auto_commit => TRUE);
    DBMS_AQADM.CREATE_QUEUE(queue_name => 'QUE_{0}', queue_table => 'QT_{0}');
    DBMS_AQADM.START_QUEUE(queue_name=> 'QUE_{0}');
END;

CREATE OR REPLACE TRIGGER TR_{0}
AFTER DELETE OR INSERT OR UPDATE{7} ON {1}
REFERENCING OLD AS OLD NEW AS NEW 
FOR EACH ROW
BEGIN
  DECLARE
    enqueue_options dbms_aq.enqueue_options_t;
    message_properties dbms_aq.message_properties_t;
    message_handle RAW(16);
    message TYPE_{0}; 
    messageType VARCHAR2(50);
    messageContent XMLType;
  BEGIN
    IF INSERTING THEN
      messageType := '{2}';
      messageContent := XMLType.CreateXML({5});
    ELSIF UPDATING THEN
      messageType := '{3}';
      messageContent := XMLType.CreateXML({5});
    ELSIF DELETING THEN      
      messageType := '{4}';
      messageContent := XMLType.CreateXML({6});
    END IF;

    message := TYPE_{0}(messageType, messageContent);
    enqueue_options.VISIBILITY := DBMS_AQ.ON_COMMIT;
    enqueue_options.SEQUENCE_DEVIATION := NULL;
    message_properties.DELAY := DBMS_AQ.NO_DELAY;
    message_properties.EXPIRATION := DBMS_AQ.NEVER;

    DBMS_AQ.ENQUEUE (
          queue_name         => 'QUE_{0}'
        , enqueue_options    => enqueue_options
        , message_properties => message_properties
        , payload            => message
        , msgid              => message_handle);
  END;
END;

BEGIN
    DECLARE
      v_object_type2 VARCHAR2(30);
    BEGIN
      SELECT   OBJECT_TYPE 
      INTO     v_object_type2 
      FROM     user_OBJECTS 
      WHERE    OBJECT_TYPE = 'TRIGGER' AND UPPER(OBJECT_NAME) = 'TR_{0}';

      EXECUTE IMMEDIATE 'DROP TRIGGER TR_{0}';
    EXCEPTION
      WHEN NO_DATA_FOUND THEN
        NULL;
    END;

    DECLARE
      v_object_type3 VARCHAR2(30);
