      INTO     v_object_type5 
      FROM     user_OBJECTS 
      WHERE    OBJECT_TYPE = 'TABLE' AND UPPER(OBJECT_NAME) = 'QT_{0}';

      DBMS_AQADM.DROP_QUEUE_TABLE(queue_table => 'QT_{0}', force => TRUE); 
    EXCEPTION
      WHEN NO_DATA_FOUND THEN
        NULL;
    END;

    DECLARE
      v_object_type6 VARCHAR2(30);
    BEGIN
